import React, { useState, useEffect } from 'react';

export default function Notification({ message, item }) {
    const notificationStyles = {
      position: 'absolute',
      // Дополнительные стили для уведомления рядом с товаром (примерно справа от него)
      top: item && item.id ? `${item.id * 100}px` : '10px',
      right: '10px',
      padding: '10px',
      background: '#fff',
      border: '1px solid #ccc',
      boxShadow: '0px 2px 5px rgba(0, 0, 0, 0.1)',
      zIndex: '100',
      transition: 'opacity 0.3s ease-in-out',
    };

    return (
      <div style={notificationStyles}>
        {message}
      </div>
    );
  }
